rhtmlPictographs::graphic(1,600,400,'{"percentage":1,"numImages":3,"variableImage":"https://s3-ap-southeast-2.amazonaws.com/kyle-public-numbers-assets/htmlwidgets/CroppedImage/blue_square_512.png","preserveAspectRatio":"xMidYMid"}')

